[ExtensionOf(classStr(SysListPanelRelationTable))]
internal final class SysListPanelRelationTable_Extension
{
    private int i;
    private SysCorpNetPrinters                      printers;
    private FilePath                                printerPath;
    private DocumentRoutingClientApp                xClientApp;
    private DocumentRoutingPrinterAppAssociation    xAppAssociation;

    private container                               printerContainer;
    private RecId                                   printerRecId;

}

Method.Source:
private void initData(container data)
{
    printerContainer = conPeek(data, i);
    printerRecId     = conPeek(printerContainer, 1);

    select printerPath from printers where printers.RecId == printerRecId;
    printerPath = printers.PrinterPath;
}

void removeData(container data)
{
    next removeData(data);

    ttsbegin;
    for (i=conlen(data); i; i--)
    {
        this.initData(data);
        while select crosscompany printers where printers.PrinterPath == printerPath
        {
            select forupdate xAppAssociation where printers.RecId == xAppAssociation.Printer && xAppAssociation.ClientApp == this.parmRelationRangeRecId();
            xAppAssociation.delete();
        }
    }
    ttscommit;
}

void addData(container data)
{
    next addData(data);

    for (i=conlen(data); i; i--)
    {
        this.initData(data);
        while select crosscompany printers where printers.PrinterPath == printerPath && printers.DataAreaId != curExt()
        {
            xAppAssociation.Printer = Printers.recId;
            xAppAssociation.ClientApp = this.parmRelationRangeRecId();
            xAppAssociation.insert();                
        }
    }       
}
